import React from "react";
import { useParams } from "react-router-dom";
import productsData from "../Data/productsData";
import ProductAdvantages from "../Components/ProductAdvantages";
import GalleryShow from "../Components/GalleryShow";

const Product = () => {
  const { productId } = useParams();
  const product = productsData.find((item) => item.id === productId);
  const productImages = product.images;
  const productAdvantages = product.pageDescription.advantages;

  return (
    <div>
      <div>
        <GalleryShow />
      </div>

      <div>{product.producer}</div>
      <div>{product.name}</div>
      <div>{product.ratings}</div>
      <div>{product.pageDescription.title}</div>

      {productAdvantages.map((advantages) => (
        <div key={advantages.item}>
          <ProductAdvantages advantages={advantages} />
        </div>
      ))}

      <div>{product.pageDescription.advantages.item}</div>

      <div>
        {product.price.amount} {product.price.currency}
      </div>
    </div>
  );
};

export default Product;
