import React, { useState } from "react";

const CartAmount = () => {
  const [count, setCount] = useState(0);


  const handleSubtractOne = () => {
    setCount(count => Math.max(count - 1, 0));
 }

  const handleAddOne = () => {
    setCount(count => Math.min(count + 1, 3));
 }

 const handleOnChange = (event) => {
    setCount(event.target.value);
 }

  return (
    <div className="pruduct-addTocart counter">
      <button onClick={handleSubtractOne}><svg focusable="false" class="css-e75gge-icon themed-icon" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M1.92896 7C1.37667 7 0.928955 7.44772 0.928955 8C0.928955 8.55228 1.37667 9 1.92896 9V7ZM14 9C14.5523 9 15 8.55228 15 8C15 7.44772 14.5523 7 14 7V9ZM1.92896 9L7.96448 9V7L1.92896 7V9ZM14 7L7.96448 7V9L14 9V7Z" fill="#C92976"></path></svg></button>
      <label for="input">
      <input id="input "type="text" value={count} onChange={handleOnChange} disabled />
      </label>
      <button onClick={handleAddOne}><svg focusable="false" class="css-e75gge-icon themed-icon" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M1.92871 8H7.96423M7.96423 8V14.0355M7.96423 8H13.9998M7.96423 8V1.96448" stroke="#C92976" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"></path></svg></button>
    </div>
  );
};

export default CartAmount;
