import { useParams } from "react-router-dom";
import productsData from "../Data/productsData";
import Cart from "./Cart";
import Delivery from "./Delivery";
import ProductInfoListIcon from "./ProductInfoListIcon";

const ProductDetails = () => {
  const { productId } = useParams();
  const product = productsData.find((item) => item.id === productId);
  const productAdvantages = product.pageDescription.advantages;
  return (
    <div className="product-info">
      <div>
        <h1>
          <div className="product-brand">{product.producer}</div>
          {product.name}
        </h1>
        <div className="product-ratings">{product.ratings}</div>
      </div>
      <div className="divider divider-horizontal"></div>
      <div className="product-description">
        <h2>{product.pageDescription.title}</h2>
        <ul>
          {productAdvantages.map((advantages) => (
            <li key={advantages.item}>
              <ProductInfoListIcon />
              <p>{advantages.item}</p>
            </li>
          ))}
        </ul>
      </div>
      <div className="divider divider-horizontal"></div>
      <div className="product-price wrapper">
        {product.price.amount} {product.price.currency}
        <div>
        (inkl. MwSt., zzgl. <a href="#versand">Versand</a>)
      </div>
      <div>Artikel Nr. {product.id}</div>
      <div>
        <Cart />
      </div>
      </div>
      
      <Delivery />
    </div>
  );
};

export default ProductDetails;
